sys_variable_value.value:
(function execute(inputs, outputs) {

var sSysId = '';
var sJSON = inputs.rest_output;

try {
	var oInput = JSON.parse(sJSON);
	sSysId = oInput['result'][0]['sys_id'];
} catch (ex) {
	gs.error('there is an error in my json: ' + String(ex));
}

outputs.ssysid = sSysId;

})(inputs, outputs);

