sys_script_include.script:
var RESTAPIforProducts = Class.create();
RESTAPIforProducts.prototype = {
	initialize: function () {
		this._gtREST = new global.GlobalToolsRESTAPI('9bc4a52e8305ae10f779ba29feaad3e2');
	},

	// This is called from Business "Rule Send To Remote System"
	insertOrUpdateProduct: function (grProduct, sAction) {
		var oResponse;
		if (sAction == 'insert') {
			var oRESTPayload = {
				number: grProduct.getValue('number'),
				name: grProduct.getValue('name'),
				description: grProduct.getValue('description'),
			};
			oResponse = this._gtREST.doRESTRequest('/api/now/table/x_669990_charlie_1_products', 'POST', oRESTPayload);
			gs.info('tomtom response insert: ' + JSON.stringify(oResponse));
		} else if (sAction == 'update') {
			try {
				var aResponse = this._gtREST.doRESTRequest('/api/now/table/x_669990_charlie_1_products?sysparm_query=number=' + grProduct.getValue('number') + '&sysparm_fields=sys_id&sysparm_limit=1', 'GET');
				var sRemoteProductId = '';
				if ((aResponse) && (Array.isArray(aResponse)) && (aResponse[0].hasOwnProperty('sys_id'))) {
					oResponse = aResponse[0];
					sRemoteProductId = oResponse['sys_id'];
				}
				gs.info('tomtom response: ' + JSON.stringify(oResponse));
				gs.info('tomtom response: ' + sRemoteProductId);
			} catch (oEx) {
				gs.error('tomtom this realy failed: ' + String(oEx));
			}

		} // insert


	}, // function insertOrUpdateProduct

	type: 'RESTAPIforProducts'
};
